import {RECEIVE_QUESTIONS, ADD_QUESTION, REMOVE_QUESTION} from "../actions/questions";


export default function questions (state = {}, action) {
    switch (action.type) {
        case RECEIVE_QUESTIONS:
            return {
                ...state,
                questions: action.questions
            }

        default : return state
    }

}
