import {RECEIVE_USERS} from "../actions/users";
import {ADD_ANSWER} from "../actions/answers";

export default function users (state = {}, action) {
    switch (action.type) {
        case RECEIVE_USERS :
            return {
            ...state,
                ...action.users}

        default : return state
    }

}
