import React, {Component} from 'react'
import {connect} from 'react-redux'
import authedUser from "../reducers/authedUser";
import {Link} from "react-router-dom";



class Question extends Component {

    render() {
        const {user, question} = this.props
        return (<Link to={`/question/${question.id}`} className={'question'}>
            <img className={'avatar'} src={user.avatarURL}/>
            <p style={{'fontWeight' : this.props.authedUser === user.id ? 'bold' : 'normal'}}>{user.name}</p>
            <div className={'question-icons'}>
            <div className={'btn'}> {question.optionOne.text}</div>
            <div className={'btn'}> {question.optionTwo.text}</div>
                {!this.props.score ? null :
                    <div className={'score'}> {this.props.score} </div>

                }
            </div>
        </Link>)
    }

}

const mapStateToProps = ({users, questions, authedUser}, {questionId, score}) => {
    const question = questions[questionId]
    const questionAuthor = question.author
    const user =  users[questionAuthor]

    console.log(question, questionAuthor, user)

    return {
        question: questions[questionId],
        user: users[questions[questionId].author],
        ...authedUser,
        score
    }
}

export default connect(mapStateToProps)(Question)
