import {addQuestion, removeQuestion} from "./questions";
import {saveQuestion, saveQuestionAnswer} from "../utilities/api";

export const ADD_ANSWER = "ADD_ANSWER"

export function addAnswer (answer) {
    return {type: ADD_ANSWER,
    answer }
}


export function handleAddAnswer (answer) {
    return (dispatch) => {
        saveQuestionAnswer(answer).then(
            () => dispatch(addAnswer(answer))).catch (
            (error) => {
                alert("There was an error: ", error)
            }
        )
    }
}
