import React, {Component} from 'react'
import {connect} from 'react-redux'
import Question from "./Question";



class Leaderboard extends Component {

    questionScore = question => {
        return question.optionOne.votes.length + question.optionTwo.votes.length
    }

    render() {

        const {questions} = this.props
        console.log("Properties: ", this.props)
        console.log(questions)

        let sortedQuestions = Object.values(questions).sort(
            (a,b) => {return this.questionScore(b) - this.questionScore(a)}
        )

        return (
            <div>
                <ul>
                    {
                        Object.keys(sortedQuestions).map(
                            (question) => (<li key={question.id}><Question questionId={question.id}/></li>)
                        )
                    }
                </ul>


            </div>

        )
    }

}

const mapStateToProps = ({questions}) => {
    return {
        questions
    }
}



export default connect(mapStateToProps)(Leaderboard)
